using namespace std;
typedef vector<int> ivec;
typedef vector<ivec> martix;
martix flip(martix b, int r, int c)
{
	reverse(b.begin(), b.end());
	return b;
}
martix rotate(martix b, int r, int c)
{
	martix a(c, ivec(r, 0));
	b = flip(b, r, c);
	for (auto i : b)
		reverse(i.begin(), i.end());
	for (int i = 0; i < c; i++)
		for (int j = 0; j < r; j++)
			a[i][j] = b[j][i];
	for (int i = 0; i < c; i++)
		reverse(a[i].begin(), a[i].end());
	return flip(a, c, r);
}
